public class InitializeAndNotInitialize {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 0;  //0이라는 값으로 초기화된 것
		int b; //변수만 선언했지, 아무 값도 담기지가 아낳음 -> 아무것도 사용이 불가함
		
		String c = null; //null이라고 초기화가 된 것
		String d;  //변수만 선언했지, 아무값도 담기지가 않음, 이 상태로는 사용이 불가능함

	}

}
